insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1000,'THE WOMEN','Kristin Hannah','https://www.robinarzon.com/','Fiction','New baby, Health & Wellness, Motherhood','N/A','https://bookshop.org/a/55613/9780316493826','https://storage.googleapis.com/du-prd/books/images/9781250178633.jpg','Little Brown Books for Young Readers','978-0316493826','St. Martin''s','2023','Head Peloton Instructor Robin Arzón follows up New York Times bestseller Strong Mama with Strong Baby, tracking the first milestones of a baby''s life, using Robin''s signature mantras as motivation.\n\nThere''s a new addition to our strong family! Now that Pequeño is born, mom and baby are ready to move together. From tummy time, to sitting up and crawling, to those exciting first steps, baby is growing stronger every day. And mom and dad are right there with her, laughing, dancing, working out, and resting. Because this family is stronger together.\nPreaching progress over perfection, Robin Arzón uses her popular Peloton mantras to pair with baby''s first physical milestones, encouraging parents and baby to move together.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1001,'THE HUNTER','Tana French','https://www.adrianabergstrom.com/','Contemporary Fiction, Fiction','Food, Multigenerational household, Grandparents, Traditions, Family, Cultural heritage','https://cdn.shopify.com/s/files/1/0550/9188/8237/files/abuelita-and-i-make-flan-activity-kit.pdf?v=1671034601','https://bookshop.org/a/55613/9781623542658','https://storage.googleapis.com/du-prd/books/images/9780593493434.jpg','','9781623542658','Viking','2022','Anita loves to bake with her abuela, especially when they are using her grandmother''s special recipes for Cuban desserts like flan!\n\nAnita is making flan for Abuelo''s birthday, but when she accidentally breaks Abuelita''s treasured flan serving plate from Cuba, she struggles with what to do. Anita knows it''s right to tell the truth, but what if Abuelita gets upset? Worried that she has already ruined the day, Anita tries to be the best helper. After cooking the flan, they need a serving dish! Anita comes up with a wonderful solution.\n\nComplete with a glossary of Spanish terms and a traditional recipe for flan, Abuelita and I Make Flan is a delicious celebration of food, culture, and family.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1002,'NEVER TOO LATE','Danielle Steel','https://www.adrianabergstrom.com/','Contemporary Fiction, Fiction','Ecosystems, Nature','','https://bookshop.org/a/55613/9781338828665','https://storage.googleapis.com/du-prd/books/images/9780593498408.jpg','Orchard Books','978-1338828665','Delacorte','2023','This gorgeous, poetic story follows the unexpected story of a lone tumbleweed making its way across the desert.');
