insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1007,'HAPPY PLACE','Emily Henry','https://www.aixaperezprado.com/about','Narrative Nonfiction, Board Book','Diversity, Cultural diversity, Identity, Travel, Cultural heritage','','','https://storage.googleapis.com/du-prd/books/images/9780593441275.jpg','','9798888590102','Berkley','2023','Come along for a day of exploring the sights and sounds of Argentina from morning light to city night. Snack on medialunas, count the stalls at la feria and ride the caballito on the carousel. Argentinian author and illustrator Aixa Pérez-Prado and Mariana Ruiz Johnson (Head, Shoulders, Knees and Toes, Run Little Chaski, The Last Hazlenut) draw on their lived experiences in Argentina to create an authentic picture of modern Argentinian life as part of the Our World series for very young readers.\n');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1008,'THREE-INCH TEETH','C.J. Box','','Fiction, Poetry/ Song','Imagination, Emotions, Self-worth','','https://bookshop.org/a/55613/9781734783919','https://storage.googleapis.com/du-prd/books/images/9780593331347.jpg','','978-1-7347839-1-9','Putnam','2020','The Invisible takes you on an interactive journey with a boy who is a master in the art of disappearing and invites young readers to explore what it would be like to not be seen at home, at school, on the street, or anywhere.');
