insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1010,'A COURT OF SILVER FLAMES','Sarah J. Maas','','Fiction','Humor','N/A','https://bookshop.org/a/55613/9780711288294','https://storage.googleapis.com/du-prd/books/images/9781681196282.jpg','Frances Lincoln Children''s Books','978-0711288294','Bloomsbury','2023','A laugh-out-loud story with heart, based on true events about one boy''s trip to the loo and learning that everyone has accidents sometimes.\n\nA father is just trying to take his kids for a refreshing swim in the pool, but every time they jump in...\n"DAD! I NEED TO POOP!"\nBy the time they get to the bathroom: "I don''t need to go anymore!" Sound familiar?\nBack and forth, back and forth they go. "Just poop already!" yells the big sister.\nUntil finally, the little brother... does. Oops!\nBased on true events in the real life of one unfortunate little brother...\nA laugh-out-loud story with heart, because when you gotta go, you gotta go!');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1011,'THE HOUSEMAID','Freida McFadden','https://alexandervidal.com','Nonfiction','Nature, Science, Animals','N/A','https://bookshop.org/a/55613/9781452184494','https://storage.googleapis.com/du-prd/books/images/9781538742570.jpg','','978-1452184494','Grand Central','2022','The definitive guide to wildlife of the United States.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1012,'FIRST LIE WINS','Ashley Elston','https://alexandraalessandri.com/','Contemporary Fiction, Fiction','Family, Traditions, Multigenerational household, Cultural heritage, Building Confidence & Self-esteem','https://alexandraalessandri.com/feliz-new-year-ava-gabriela/teachers-guide-activities/','https://bookshop.org/a/55613/9780807504505','https://storage.googleapis.com/du-prd/books/images/9780593492918.jpg','','978-0807504505','Pamela Dorman','2020','Ava''s excited to say goodbye to el Año Viejo- but will her shyness keep her from joining in the celebration?');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1013,'THE HEAVEN & EARTH GROCERY STORE','James McBride','https://alexandraalessandri.com/','Contemporary Fiction, Fiction','Communication, Building Confidence & Self-esteem, Social & Emotional Learning','https://apalessandri.files.wordpress.com/2021/09/isabel_teacherguide.pdf','https://bookshop.org/a/55613/9781534110632','https://storage.googleapis.com/du-prd/books/images/9780593422946.jpg','Sleeping Bear Press','978-1534110632','Riverhead','2021','English, with its blustery blues and whites, just feels wrong to Isabel. She prefers the warm oranges and pinks of Spanish. As she prepares for class at a new school, she knows she''s going to have to learn- and she would rather not! Her first day is uncomfortable, until she discovers there''s more than one way to communicate with friends.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1014,'THIS COULD BE US','Kennedy Ryan','https://alexandraalessandri.com/','Contemporary Fiction, Fiction','Social & Emotional Learning, Empathy, Nature','','','https://storage.googleapis.com/du-prd/books/images/9781538706824.jpg','Beaming Books','','Forever','2024','A hurricane is coming and as everyone in her coastal neighborhood prepares, Lupita Lopez channels her worry into caring for a pair of abandoned baby pigeons.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1015,'THE HOUSE OF HIDDEN MEANINGS','RuPaul','www.alexandrakatona.com','Contemporary Fiction, Fiction','Family, Traditions, Language barriers, Building Confidence & Self-esteem','N/A','https://bookshop.org/a/55613/9781646862931','https://storage.googleapis.com/du-prd/books/images/9780063263901.jpg','','978-1646862931','Dey Street','2021','Warm memories wash over a first-generation Latinx American girl as she experiences a typical Sunday night dinner at her Abuelita''s house. As Alejandra thinks about all the good times her family has had there, she decides that she wants to be brave and try speaking Spanish with Abuelita so that they can deepen their bond.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1016,'BLOOD MONEY','Peter Schweizer','','Bilingual, Fiction','Immigration, Father-son relationship','https://www.barefootbooks.com/blog/post/2020-06-diversity-and-inclusion','https://bookshop.org/a/55613/9781782853923','https://storage.googleapis.com/du-prd/books/images/9780063061194.jpg','','978-1782853886','Harper','2018','Join a young boy and his father on a daring journey from Mexico to Texas to find a new life. They''ll need all the resilience and courage they can muster to safely cross the border - la frontera - and to make a home for themselves in a new land.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1017,'THE WAGER','David Grann','https://alicia-salazar.com/services/','Fantasy, Fiction','Urban Legends, Nature','N/A','https://bookshop.org/a/55613/9781663909664','https://storage.googleapis.com/du-prd/books/images/9780385534260.jpg','Picture Window Books','978-1663909664','Doubleday','2021','Look carefully! A dark shadow moves through the lapping lake water. It’s the Loch Ness Monster! This shy serpent hides away from humans and treats lake dwellers with kindness. Ever wondered about Nessie’s diet and other habits? Does the lake monster have companions? Wonder no more! Striking illustrations and matter-of-fact text take you on an underwater journey to learn all about the Loch Ness Monster.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1018,'THE TRUMP INDICTMENTS','with an introduction, annotations and supporting materials  Melissa Murray and Andrew Weissmann','https://alicia-salazar.com/services/','Fantasy, Fiction','Urban Legends, Mythology & Folklore','N/A','','https://storage.googleapis.com/du-prd/books/images/9781324079200.jpg','Picture Window Books','978-1663909701','Norton','2021','What’s that steady booming echoing through the mountains? It’s the heavy footsteps of a troll. Humans should beware of these creatures’ terrible, destructive powers. Have you ever wondered how trolls are born and grow up? What foods they prefer to feast on? What mischief fills their time? Wonder no more! Striking illustrations and matter-of-fact text take you on daring journey to learn all about trolls.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1019,'KILLERS OF THE FLOWER MOON','David Grann','https://alicia-salazar.com/services/','Nonfiction','Holidays, Mexican culture, Cultural heritage','N/A','https://bookshop.org/a/55613/9781663908278','https://storage.googleapis.com/du-prd/books/images/9780385534246.jpg','Pebble Books','978-1663908278','Doubleday','2021','Día de los Muertos is a special holiday to remember and celebrate loved ones. It is a Mexican tradition held to honor those who have died. Some people dress up and go to parades. Others sing, dance, and eat special food. Others decorate altars with flowers and make offerings. Readers will discover how a shared holiday can have multiple traditions and be celebrated in all sorts of ways.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1020,'OUTLIVE','Peter Attia with Bill Gifford','http://almaflorada.com/','Contemporary Fiction, Fiction','Family, Multicultural, Bicultural Identity, Biracial identity','http://almaflorada.com/i-love-saturdays-y-domingos/','https://bookshop.org/a/55613/9780689874093','https://storage.googleapis.com/du-prd/books/images/9780593236598.jpg','Atheneum Books for Young Readers','978-0689870493','Harmony','2004','Saturdays and Sundays and very special days for the child in this story. On Saturdays she visits Grandma and Grandpa, who come from a European-American background, and on Sundays - los domingos - she visits Abuelito y Abuelita, who are Mexican-American. While the two sets of grandparents are different in many ways, they also have a great deal in common - in particular, their love for their granddaughter.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1021,'THE BODY KEEPS THE SCORE','Bessel van der Kolk','https://www.almaflorada.com','Poetry/ Song','Nursery rhymes, Rhyming, Lullabies, Songs','','https://bookshop.org/a/55613/9780316540209','https://storage.googleapis.com/du-prd/books/images/9780670785933.jpg','Little Brown Books for Young Readers','978-0316540209','Penguin','2023','A unique bilingual treasury full of charm and humor that includes more than 80 rhymes and lullabies from across the Spanish-speaking world.\n \nThis delightfully illustrated full-color collection includes beloved lullabies, jump rope songs, riddles, and proverbs collected by award-winning authors and scholars of Latine literature. Rich with the diversity of Latine cultures, this treasury serves as both a warm introduction to Latine folklore for English speakers and a trove of familiar favorites for Spanish speakers.\n \nThe collection also includes sheet music and bonus content for caregivers to enrich the reading experience.\n\nEste libro es un tesoro bilingüe único, lleno de encanto y humor, que contiene más de 80 rimas y canciones de cuna de todo el mundo hispanohablante.\n \nEsta colección hermosamente ilustrada a todo color contiene entrañables canciones de cuna, canciones para saltar la cuerda, adivinanzas y refranes recolectados por galardonados autores y estudiosos de la literatura latine. Este tesoro, ricamente diverso gracias a las culturas hispanas, sirve como un cálido acercamiento al folclor latine para los hablantes de inglés y como una joya de obras favoritas familiares para los hispanohablantes.\n \nEsta colección también incluye partituras y contenido extra para que los cuidadores puedan enriquecer la experiencia de lectura.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1022,'BURN BOOK','Kara Swisher','https://www.alyssaauthor.com/','Contemporary Fiction, Fiction','Ancestors, Food, Caribbean culture, Cultural heritage, Family','N/A','https://bookshop.org/a/55613/9781665902731','https://storage.googleapis.com/du-prd/books/images/9781982163891.jpg','Atheneum Books for Young Readers','978-1665902731','Simon & Schuster','2023','A delicious picture book about the ways plantains shape Latinx culture, community, and family, told through a young girl''s experiences in the kitchen with her Abuela.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1023,'OATH AND HONOR','Liz Cheney','https://www.alyssaauthor.com/','Contemporary Fiction, Fiction','Community, Cultural heritage','N/A','https://bookshop.org/a/55613/9780316397735','https://storage.googleapis.com/du-prd/books/images/9780316572064.jpg','Little Brown Books for Young Readers','978-0316397735','Little, Brown','2023','Welcome to The Bronx, New York! A picture book celebration of hometown pride including the history, landscape, cuisines, cultures and activities unique to this vibrant community.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1024,'BAD THERAPY','Abigail Shrier','','Contemporary Fiction, Fiction','Family, Social & Emotional Learning, Grandparents, Immigration','N/A','https://bookshop.org/a/55613/9780873589147','https://storage.googleapis.com/du-prd/books/images/9780593542927.jpg','Cooper Square Pub','978-0873589147','Sentinel','2007','Jose is not sure what to expect when his grandmother from Mexico moves in... except that he will certainly have to change his ways. What he discovers is that he doesn''t mind one bit. Abuelita is full of surprises! She is full of life!');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1025,'THE BOYS IN THE BOAT','Daniel James Brown','','Contemporary Fiction, Fiction','Family, Nature, Mother-son relationships, Bedtime','N/A','https://bookshop.org/a/55613/9780873589277','https://storage.googleapis.com/du-prd/books/images/9781101622742.jpg','Cooper Square Pub','978-0873589277','Penguin','2007','Just after the sun has set, mama takes her hijo for a stroller walk down the lane, across the orchard, past the lake, through the woods, and back home again. The pair greets everything they pass along the way with a hello and an hola. The night, in kind, welcomes them with many of its hidden treasures.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1026,'WHITE RURAL RAGE','Tom Schaller and Paul Waldman','https://www.amyraleon.com','Poetry/ Song, Fiction','Freedom, Building Confidence & Self-esteem, Hope','N/A','https://bookshop.org/a/55613/9781912497324','https://storage.googleapis.com/du-prd/books/images/9780593729144.jpg','Flying Eye Books','978-1912497324','Random House','2020','As powerful as it is beautiful, Freedom, We Sing is a lyrical picture book designed to inspire and give hope to readers around the world. It''s the perfect book for parents who want a way to gently start the conversation with their kids about finding hope in these very tense times we are living in.');
insert into books (id, title, author, author_website,genre, theme, educator_guide,purchase_link,cover,imprint,isbn, publisher, publish_year,summary) values(1027,'10% HAPPIER','Dan Harris','https://anaranda.com/','Fiction','Day of the Dead, Family, Family traditions, Holidays, Cultural heritage','N/A','https://bookshop.org/a/55613/9780525514282','https://storage.googleapis.com/du-prd/books/images/9780063356474.jpg','Nancy Paulsen Books','978-0525514282','Dey Street','2022','A family honors their living and dead relatives as they celebrate this holiday with shared food and stories.');
